'use strict';

/*
 * @api public
 * @property {function} format
 * Both the construction method and set of exposed
 * formats.
 */
const format = exports.format = require('logform/format');

/*
 * @api public
 * @method {function} levels
 * Registers the specified levels with logform.
 */
exports.levels = require('logform/levels');

//
// Setup all transports as eager-loaded exports
// so that they are static for the bundlers.
//
Object.defineProperty(format, 'align',       { value: require('logform/align') });
Object.defineProperty(format, 'cli',         { value: require('logform/cli') });
Object.defineProperty(format, 'colorize',    { value: require('logform/colorize') });
Object.defineProperty(format, 'combine',     { value: require('logform/combine') });
Object.defineProperty(format, 'errors',      { value: require('logform/errors') });
Object.defineProperty(format, 'json',        { value: require('logform/json') });
Object.defineProperty(format, 'label',       { value: require('logform/label') });
Object.defineProperty(format, 'logstash',    { value: require('logform/logstash') });
Object.defineProperty(format, 'metadata',    { value: require('logform/metadata') });
Object.defineProperty(format, 'ms',          { value: require('logform/ms') });
Object.defineProperty(format, 'padLevels',   { value: require('logform/pad-levels') });
Object.defineProperty(format, 'prettyPrint', { value: require('logform/pretty-print') });
Object.defineProperty(format, 'printf',      { value: require('logform/printf') });
Object.defineProperty(format, 'simple',      { value: require('logform/simple') });
Object.defineProperty(format, 'splat',       { value: require('logform/splat') });
Object.defineProperty(format, 'timestamp',   { value: require('logform/timestamp') });
Object.defineProperty(format, 'uncolorize',  { value: require('logform/uncolorize') });
